mapper.insert:
INSERT INTO community (
    title, content, user_idx
) VALUES (
    #{title}, #{content}, #{userIdx}
)

mapper.select:
SELECT * FROM community
LEFT JOIN user ON community.user_idx = user.user_idx
WHERE deleted = 0 AND division = 1 ORDER BY created_at DESC;

mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO community (
    title, content, user_idx, disclosure, division
) VALUES (
    #{title}, #{content}, #{userIdx}, #{disclosure}, #{division}
)

INSERT INTO community_image (
    community_idx, image_url
) VALUES (
    #{communityIdx}, #{imageUrl}
)

INSERT INTO community_comment (
    user_idx, community_idx, comment
) VALUES (
    #{userIdx}, #{communityIdx}, #{comment}
)
